catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
		g_form.clearOptions('seat_numbers');
		g_form.clearValue("cost");
        return;
    }

    g_form.clearOptions("seat_number");
	g_form.clearValue("cost");
    var seatTypes = new GlideAjax("GetAvailableSeats");
    seatTypes.addParam('sysparm_name', 'getAvailableSeats');
    seatTypes.addParam('sysparm_seat_type', newValue);
    seatTypes.addParam('sysparm_theater', g_form.getValue('theater'));
    seatTypes.addParam('sysparm_movie', g_form.getValue('movie'));
    seatTypes.addParam('sysparm_date', g_form.getValue('date'));
    seatTypes.addParam('sysparm_time', g_form.getValue('time'));
    seatTypes.getXMLAnswer(populateSeats);

    function populateSeats(response) {
        var set = new Set();
        var seats = JSON.parse(response);
		if(seats.length > 0)
			g_form.addOption("seat_number", '', 'Please select seat number');
        for (var i = 0; i < seats.length; i++) {
            if (set.has(seats[i]) == false)
                g_form.addOption("seat_number", seats[i], seats[i]);
            set.add(seats[i]);
        }
    }

}
