catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    g_form.hideFieldMsg('date');
    var getReleaseDate = new GlideAjax('GetReleaseDate');
    getReleaseDate.addParam('sysparm_name', 'getReleaseDate');
    getReleaseDate.addParam('sysparm_movie_name', g_form.getValue('movie'));
    getReleaseDate.getXMLAnswer(populateReleaseDate);

    function populateReleaseDate(response) {
        var configuredDate = g_form.getValue('date');
        if (new Date(configuredDate) < new Date(response)) {
            g_form.setValue('date', '');
            g_form.showFieldMsg('date', 'Movie Release date is ' + response + ' but configured date is ' + configuredDate + " which is earlier than release date", true);
        }
    }
}
