catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '' || g_form.getValue('movie') == '' || g_form.getValue('date') == '' || g_form.getValue('time') == '' || g_form.getValue('theater') == '') {
        g_form.clearOptions("seat_type");
        g_form.clearOptions("seat_numbers");
        g_form.hideFieldMsg('seat_number');
        return;
    }
    g_form.clearOptions("seat_type");
    g_form.hideFieldMsg('seat_number');
    g_form.clearOptions("seat_number");
    var seatTypes = new GlideAjax("SeatTypes");
    seatTypes.addParam('sysparm_name', 'getSeatTypes');
    seatTypes.addParam('sysparm_theater_name', newValue);
    seatTypes.addParam('sysparm_movie_name', g_form.getValue('movie'));
    seatTypes.addParam('sysparm_date', g_form.getValue('date'));
    seatTypes.addParam('sysparm_time', g_form.getValue('time'));
    seatTypes.getXMLAnswer(populateSeats);

    function populateSeats(response) {
        var set = new Set();
        var seats = JSON.parse(response);
        if (seats.length > 0)
            g_form.addOption("seat_type", '', 'Please select seat type');
        for (var i = 0; i < seats.length; i++) {
            if (set.has(seats[i].sys_id) == false) {
                g_form.addOption("seat_type", seats[i].sys_id, seats[i].name);
            }
            set.add(seats[i].sys_id);
        }
    }


}
