catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '' || g_form.getValue('movie') == '' || g_form.getValue('date') == '' || g_form.getValue('time') == '' || g_form.getValue('seat_number') == '') {
        return;
    }
	g_form.clearValue("cost");
    var seatTypes = new GlideAjax("GetPriceDetails");
    seatTypes.addParam('sysparm_name','getPriceDetails');
    seatTypes.addParam('sysparm_theater_name', g_form.getValue('theater'));
    seatTypes.addParam('sysparm_movie_name', g_form.getValue('movie'));
    seatTypes.addParam('sysparm_date', g_form.getValue('date'));
    seatTypes.addParam('sysparm_time', g_form.getValue('time'));
	seatTypes.addParam('sysparm_seat_type',g_form.getValue('seat_type'));
    seatTypes.getXMLAnswer(getPrice);

    function getPrice(response) {
		g_form.setValue("cost",response,response);
    }
}
