sys_script_include.script:
var GetReleaseDate = Class.create();
GetReleaseDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getReleaseDate: function() {
        var movieRecord = new GlideRecord("x_1509691_movie_0_movie");
		movieRecord.addQuery('sys_id',this.getParameter('sysparm_movie_name'));
        movieRecord.query();
        if (movieRecord.next()) {
			return movieRecord.release_date.toString();
		}
        return "";
    },
    type: 'GetReleaseDate'
});
