sys_script_include.script:
var GetTheaters = Class.create();
GetTheaters.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getTheaters: function() {
		var theaterRecord = new GlideRecord("x_1509691_movie_0_theater_configuration");
		theaterRecord.addQuery("movie",this.getParameter("sysparm_movie_name"));
		theaterRecord.addQuery("date",this.getParameter("sysparm_date"));
		theaterRecord.addQuery("time",this.getParameter("sysparm_time"));
		theaterRecord.query();
		var theaters = [];
		while(theaterRecord.next()){
			var theaterDetails = {
			};
			theaterDetails.name = theaterRecord.theater.name.toString();
			theaterDetails.sys_id = theaterRecord.theater.sys_id.toString(); 
			theaters.push(theaterDetails);
		}
		return JSON.stringify(theaters);
    },
	getTheatersOwnedByUser: function() {
		var theaterRecord = new GlideRecord("x_1509691_movie_0_theater");
		theaterRecord.addQuery("owner",gs.getUserID());
		theaterRecord.query();
		var theaters = [];
		while(theaterRecord.next()){
			var theaterDetails = {
			};
			theaterDetails.name = theaterRecord.name.toString();
			theaterDetails.sys_id = theaterRecord.sys_id.toString(); 
			theaters.push(theaterDetails);
		}
		return JSON.stringify(theaters);
    },
    type: 'GetTheaters'
});
