sys_script_include.script:
var SeatTypes = Class.create();
SeatTypes.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getSeatTypes: function() {
        var movie = this.getParameter('sysparm_movie_name');
        var theater = this.getParameter('sysparm_theater_name');
        var date = this.getParameter('sysparm_date');
        var time = this.getParameter('sysparm_time');

        var theaterRecord = new GlideRecord("x_1509691_movie_0_theater_configuration");
        theaterRecord.addQuery('movie', movie);
        theaterRecord.addQuery('theater', theater);
        theaterRecord.addQuery('date', date);
        theaterRecord.addQuery('time', time);
        theaterRecord.query();
        var seatTypes = [];
        while (theaterRecord.next()) {
            var seatDetails = {};
            seatDetails.name = theaterRecord.seat_type.name.toString();
            seatDetails.sys_id = theaterRecord.seat_type.sys_id.toString();
            seatTypes.push(seatDetails);
        }
        return JSON.stringify(seatTypes);
    },
    getSeatTypesConfiguredForTheater: function() {
        var theater = this.getParameter('sysparm_theater');
        var seatsConfguration = new GlideRecord("x_1509691_movie_0_seats_configuration");
        seatsConfguration.addQuery('theater', theater);
        seatsConfguration.query();
        var seatTypes = [];
        while (seatsConfguration.next()) {
            var seatDetails = {};
            seatDetails.name = seatsConfguration.seat_type.name.toString();
            seatDetails.sys_id = seatsConfguration.seat_type.sys_id.toString();
            seatTypes.push(seatDetails);
        }
        return JSON.stringify(seatTypes);
    },
    type: 'SeatTypes'
});
