sys_script_include.script:
var validateDateUtil = Class.create();
validateDateUtil.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    validateDate: function() {
        var userSelectedDate = new GlideDateTime(this.getParameter('sysparm_date'));
		var currentDate = new GlideDateTime();
		if(userSelectedDate < currentDate){
			return "false";
		}
		return "true";
    },
    type: 'validateDateUtil'
});
