sys_script_include.script:
var GetAvailableSeats = Class.create();
GetAvailableSeats.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getAvailableSeats: function() {
        try {
            var availableSeats = [];
            var seatType = new GlideRecord("x_1509691_movie_0_seats_configuration");
            seatType.addQuery("theater", this.getParameter("sysparm_theater"));
            seatType.addQuery("seat_type", this.getParameter("sysparm_seat_type"));
            seatType.query();
            if (seatType.next()) {
                var startSeatNumber = Number(seatType.start_seat_number);
                var endSeatNumber = Number(seatType.end_seat_number);
                for (var i = startSeatNumber; i <= endSeatNumber; i++) {
                    availableSeats.push(i);
                }
            }
            var booking = new GlideRecord("x_1509691_movie_0_booking");
            booking.addQuery("theater", this.getParameter("sysparm_theater"));
            booking.addQuery("movie", this.getParameter("sysparm_movie"));
            booking.addQuery("date", this.getParameter("sysparm_date"));
            booking.addQuery("time", this.getParameter("sysparm_time"));
            booking.query();
            while (booking.next()) {
                var index = availableSeats.indexOf(JSON.parse(booking.seat_number));
                if (index != -1)
                    availableSeats.splice(index, 1);
            }
        } catch (err) {
            gs.error(err);
        }
        return JSON.stringify(availableSeats);

    },
    type: 'GetAvailableSeats'
});
